-- REPLACE INTO `STANDARD_MONSTERS` VALUES (47,'Black Dragon Wyrmling','Medium','dragon','','chaotic evil','17','33','6d8','30 ft., fly 60 ft., swim 30 ft.','15','14','13','10','11','13','','4','3','','2','3.0','','','','','','','','','','','','4','','','4','','Draconic','blindsight 10 ft., darkvision 60 ft., passive Perception 14','2','','','acid','','<html><br><li><br><name><text><b>Amphibious</text></name><br><desc><text><i>The dragon can breathe air and water.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 7 (1d10 + 2) piercing damage plus 2 (1d4) acid damage.</text></desc><br><attack_bonus>4</attack_bonus><br><damage_dice><text>1d10</text></damage_dice><br><damage_bonus>2</damage_bonus><br></li><br><li><br><name><text><b>Acid Breath (Recharge 5-6)</text></name><br><desc><text><i>The dragon exhales acid in a 15-foot line that is 5 feet wide. Each creature in that line must make a DC 11 Dexterity saving throw, taking 22 (Sd8) acid damage on a failed save, or half as much damage on a successful one.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>5d8</text></damage_dice><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (48,'Black Pudding','Large','ooze','','unaligned','7','85','10d10','20 ft., climb 20 ft.','16','5','16','1','6','1','','','','','','','','','','','','','','','','','','','','','','','','blindsight 60 ft. (blind beyond this radius), passive Perception 8','4','','','acid, cold, lightning, slashing','blinded, charmed, deafened, exhaustion, frightened, prone','<html><br><li><br><name><text><b>Amorphous</text></name><br><desc><text><i>The pudding can move through a space as narrow as 1 inch wide without squeezing.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Corrosive Form</text></name><br><desc><text><i>A creature that touches the pudding or hits it with a melee attack while within 5 feet of it takes 4 (1d8) acid damage. Any nonmagical weapon made of metal or wood that hits the pudding corrodes. After dealing damage, the weapon takes a permanent and cumulative -1 penalty to damage rolls. If its penalty drops to -5, the weapon is destroyed. Nonmagical ammunition made of metal or wood that hits the pudding is destroyed after dealing damage. The pudding can eat through 2-inch-thick, nonmagical wood or metal in 1 round.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>1d8</text></damage_dice><br></li><br><li><br><name><text><b>Spider Climb</text></name><br><desc><text><i>The pudding can climb difficult surfaces, including upside down on ceilings, without needing to make an ability check.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','<html><br><li><br><name><text><b>Split</text></name><br><desc><text><i>When a pudding that is Medium or larger is subjected to lightning or slashing damage, it splits into two new puddings if it has at least 10 hit points. Each new pudding has hit points equal to half the original pudding''s, rounded down. New puddings are one size smaller than the original pudding.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','<html><br><li><br><name><text><b>Pseudopod</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 6 (1d6 + 3) bludgeoning damage plus 18 (4d8) acid damage. In addition, nonmagical armor worn by the target is partly dissolved and takes a permanent and cumulative -1 penalty to the AC it offers. The armor is destroyed if the penalty reduces its AC to 10.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>1d6 + 4d8</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (49,'Blink Dog','Medium','fey','','lawful good','13','22','4d8','40 ft.','12','17','12','10','13','11','','','','','','','','','','','','','','','','','','3','','','5','','Blink Dog, understands Sylvan but can''t speak it','passive Perception 10','1/4','','','','','<html><br><li><br><name><text><b>Keen Hearing and Smell</text></name><br><desc><text><i>The dog has advantage on Wisdom (Perception) checks that rely on hearing or smell.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 4 (1d6 + 1) piercing damage.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br><li><br><name><text><b>Teleport (Recharge 4-6)</text></name><br><desc><text><i>The dog magically teleports, along with any equipment it is wearing or carrying, up to 40 ft. to an unoccupied space it can see. Before or after teleporting, the dog can make one bite attack.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
