-- REPLACE INTO `STANDARD_MONSTERS` VALUES (169,'Hobgoblin','Medium','humanoid','goblinoid','lawful evil','18','11','2d8','30 ft.','13','12','12','10','10','9','','','','','','','','','','','','','','','','','','','','','','','Common, Goblin','darkvision 60 ft., passive Perception 10','1/2','','','','','<html><br><li><br><name><text><b>Martial Advantage</text></name><br><desc><text><i>Once per turn, the hobgoblin can deal an extra 7 (2d6) damage to a creature it hits with a weapon attack if that creature is within 5 ft. of an ally of the hobgoblin that isn''t incapacitated.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>2d6</text></damage_dice><br></li><br></html>','','<html><br><li><br><name><text><b>Longsword</text></name><br><desc><text><i>Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 5 (1d8 + 1) slashing damage, or 6 (1d10 + 1) slashing damage if used with two hands.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d8</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br><li><br><name><text><b>Longbow</text></name><br><desc><text><i>Ranged Weapon Attack: +3 to hit, range 150/600 ft., one target. Hit: 5 (1d8 + 1) piercing damage.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d8</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (170,'Homunculus','Tiny','construct','','neutral','13','5','2d4','20 ft., fly 40 ft.','4','15','11','10','10','7','','','','','','','','','','','','','','','','','','','','','','','understands the languages of its creator but can''t speak','darkvision 60 ft., passive Perception 10','0','','','poison','charmed, poisoned','<html><br><li><br><name><text><b>Telepathic Bond</text></name><br><desc><text><i>While the homunculus is on the same plane of existence as its master, it can magically convey what it senses to its master, and the two can communicate telepathically.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +4 to hit, reach 5 ft., one creature. Hit: 1 piercing damage, and the target must succeed on a DC 10 Constitution saving throw or be poisoned for 1 minute. If the saving throw fails by 5 or more, the target is instead poisoned for 5 (1d10) minutes and unconscious while poisoned in this way.</text></desc><br><attack_bonus>4</attack_bonus><br><damage_bonus>1</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (171,'Horned Devil','Large','fiend','devil','lawful evil','18','148','17d10','20 ft., fly 60 ft.','22','17','21','12','16','17','10','7','','','7','7.0','','','','','','','','','','','','','','','','','Infernal, telepathy 120 ft.','darkvision 120 ft., passive Perception 13','11','','cold; bludgeoning, piercing, and slashing from nonmagical weapons that aren''t silvered','fire, poison','poisoned','<html><br><li><br><name><text><b>Devil''s Sight</text></name><br><desc><text><i>Magical darkness doesn''t impede the devil''s darkvision.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Magic Resistance</text></name><br><desc><text><i>The devil has advantage on saving throws against spells and other magical effects.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The devil makes three melee attacks: two with its fork and one with its tail. It can use Hurl Flame in place of any melee attack.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Fork</text></name><br><desc><text><i>Melee Weapon Attack: +10 to hit, reach 10 ft., one target. Hit: 15 (2d8 + 6) piercing damage.</text></desc><br><attack_bonus>10</attack_bonus><br><damage_dice><text>2d8</text></damage_dice><br><damage_bonus>6</damage_bonus><br></li><br><li><br><name><text><b>Tail</text></name><br><desc><text><i>Melee Weapon Attack: +10 to hit, reach 10 ft., one target. Hit: 10 (1d8 + 6) piercing damage. If the target is a creature other than an undead or a construct, it must succeed on a DC 17 Constitution saving throw or lose 10 (3d6) hit points at the start of each of its turns due to an infernal wound. Each time the devil hits the wounded target with this attack, the damage dealt by the wound increases by 10 (3d6). Any creature can take an action to stanch the wound with a successful DC 12 Wisdom (Medicine) check. The wound also closes if the target receives magical healing.</text></desc><br><attack_bonus>10</attack_bonus><br><damage_dice><text>1d8</text></damage_dice><br><damage_bonus>6</damage_bonus><br></li><br><li><br><name><text><b>Hurl Flame</text></name><br><desc><text><i>Ranged Spell Attack: +7 to hit, range 150 ft., one target. Hit: 14 (4d6) fire damage. If the target is a flammable object that isn''t being worn or carried, it also catches fire.</text></desc><br><attack_bonus>7</attack_bonus><br><damage_dice><text>4d6</text></damage_dice><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (172,'Hunter Shark','Large','beast','','unaligned','12','45','6d10','swim 40 ft.','18','13','15','1','10','4','','','','','','','','','','','','','','','','','','2','','','','','','darkvision 30 ft., passive Perception 12','2','','','','','<html><br><li><br><name><text><b>Blood Frenzy</text></name><br><desc><text><i>The shark has advantage on melee attack rolls against any creature that doesn''t have all its hit points.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Water Breathing</text></name><br><desc><text><i>The shark can breathe only underwater.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 13 (2d8 + 4) piercing damage.</text></desc><br><attack_bonus>6</attack_bonus><br><damage_dice><text>2d8</text></damage_dice><br><damage_bonus>4</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (173,'Hydra','Huge','monstrosity','','unaligned','15','172','15d12','30 ft., swim 30 ft.','20','12','20','2','10','7','','','','','','','','','','','','','','','','','','6','','','','','','darkvision 60 ft., passive Perception 16','8','','','','','<html><br><li><br><name><text><b>Hold Breath</text></name><br><desc><text><i>The hydra can hold its breath for 1 hour.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Multiple Heads</text></name><br><desc><text><i>The hydra has five heads. While it has more than one head, the hydra has advantage on saving throws against being blinded, charmed, deafened, frightened, stunned, and knocked unconscious.
Whenever the hydra takes 25 or more damage in a single turn, one of its heads dies. If all its heads die, the hydra dies.
At the end of its turn, it grows two heads for each of its heads that died since its last turn, unless it has taken fire damage since its last turn. The hydra regains 10 hit points for each head regrown in this way.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Reactive Heads</text></name><br><desc><text><i>For each head the hydra has beyond one, it gets an extra reaction that can be used only for opportunity attacks.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Wakeful</text></name><br><desc><text><i>While the hydra sleeps, at least one of its heads is awake.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The hydra makes as many bite attacks as it has heads.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +8 to hit, reach 10 ft., one target. Hit: 10 (1d10 + 5) piercing damage.</text></desc><br><attack_bonus>8</attack_bonus><br><damage_dice><text>1d10</text></damage_dice><br><damage_bonus>5</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (174,'Hyena','Medium','beast','','unaligned','11','5','1d8','50 ft.','11','13','12','2','12','5','','','','','','','','','','','','','','','','','','3','','','','','','passive Perception 13','0','','','','','<html><br><li><br><name><text><b>Pack Tactics</text></name><br><desc><text><i>The hyena has advantage on an attack roll against a creature if at least one of the hyena''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Weapon Attack: +2 to hit, reach 5 ft., one target. Hit: 3 (1d6) piercing damage.</text></desc><br><attack_bonus>2</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (175,'Ice Devil','Large','fiend','devil','lawful evil','18','180','19d10','40 ft.','21','14','18','18','15','18','','7','9','','7','9.0','','','','','','','','','','','','','','','','','Infernal, telepathy 120 ft.','blindsight 60 ft., darkvision 120 ft., passive Perception 12','14','','bludgeoning, piercing, and slashing from nonmagical weapons that aren''t silvered','fire, poison','poisoned','<html><br><li><br><name><text><b>Devil''s Sight</text></name><br><desc><text><i>Magical darkness doesn''t impede the devil''s darkvision.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Magic Resistance</text></name><br><desc><text><i>The devil has advantage on saving throws against spells and other magical effects.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The devil makes three attacks: one with its bite, one with its claws, and one with its tail.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +10 to hit, reach 5 ft., one target. Hit: 12 (2d6 + 5) piercing damage plus 10 (3d6) cold damage.</text></desc><br><attack_bonus>10</attack_bonus><br><damage_dice><text>2d6 + 3d6</text></damage_dice><br><damage_bonus>5</damage_bonus><br></li><br><li><br><name><text><b>Claws</text></name><br><desc><text><i>Melee Weapon Attack: +10 to hit, reach 5 ft., one target. Hit: 10 (2d4 + 5) slashing damage plus 10 (3d6) cold damage.</text></desc><br><attack_bonus>10</attack_bonus><br><damage_dice><text>2d4 + 3d6</text></damage_dice><br><damage_bonus>5</damage_bonus><br></li><br><li><br><name><text><b>Tail</text></name><br><desc><text><i>Melee Weapon Attack:+10 to hit, reach 10 ft., one target. Hit: 12 (2d6 + 5) bludgeoning damage plus 10 (3d6) cold damage.</text></desc><br><attack_bonus>10</attack_bonus><br><damage_dice><text>2d6 + 3d6</text></damage_dice><br><damage_bonus>5</damage_bonus><br></li><br><li><br><name><text><b>Wall of Ice</text></name><br><desc><text><i>The devil magically forms an opaque wall of ice on a solid surface it can see within 60 feet of it. The wall is 1 foot thick and up to 30 feet long and 10 feet high, or it''s a hemispherical dome up to 20 feet in diameter.
When the wall appears, each creature in its space is pushed out of it by the shortest route. The creature chooses which side of the wall to end up on, unless the creature is incapacitated. The creature then makes a DC 17 Dexterity saving throw, taking 35 (10d6) cold damage on a failed save, or half as much damage on a successful one.
The wall lasts for 1 minute or until the devil is incapacitated or dies. The wall can be damaged and breached; each 10-foot section has AC 5, 30 hit points, vulnerability to fire damage, and immunity to acid, cold, necrotic, poison, and psychic damage. If a section is destroyed, it leaves behind a sheet of frigid air in the space the wall occupied. Whenever a creature finishes moving through the frigid air on a turn, willingly or otherwise, the creature must make a DC 17 Constitution saving throw, taking 17 (5d6) cold damage on a failed save, or half as much damage on a successful one. The frigid air dissipates when the rest of the wall vanishes.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (176,'Ice Mephit','Small','elemental','','neutral evil','11','21','6d6','30 ft., fly 30 ft.','7','13','10','9','11','12','','','','','','','','','','','','','','','','','','2','','','3','','Aquan, Auran','darkvision 60 ft., passive Perception 12','1/2','bludgeoning, fire','','cold, poison','poisoned','<html><br><li><br><name><text><b>Death Burst</text></name><br><desc><text><i>When the mephit dies, it explodes in a burst of jagged ice. Each creature within 5 ft. of it must make a DC 10 Dexterity saving throw, taking 4 (1d8) slashing damage on a failed save, or half as much damage on a successful one.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>1d8</text></damage_dice><br></li><br><li><br><name><text><b>False Appearance</text></name><br><desc><text><i>While the mephit remains motionless, it is indistinguishable from an ordinary shard of ice.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Innate Spellcasting (1/Day)</text></name><br><desc><text><i>The mephit can innately cast fog cloud, requiring no material components. Its innate spellcasting ability is Charisma.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Claws</text></name><br><desc><text><i>Melee Weapon Attack: +3 to hit, reach 5 ft., one creature. Hit: 3 (1d4 + 1) slashing damage plus 2 (1d4) cold damage.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br><li><br><name><text><b>Frost Breath (Recharge 6)</text></name><br><desc><text><i>The mephit exhales a 15-foot cone of cold air. Each creature in that area must succeed on a DC 10 Dexterity saving throw, taking 5 (2d4) cold damage on a failed save, or half as much damage on a successful one.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Variant: Summon Mephits (1/Day)</text></name><br><desc><text><i>The mephit has a 25 percent chance of summoning 1d4 mephits of its kind. A summoned mephit appears in an unoccupied space within 60 feet of its summoner, acts as an ally of its summoner, and can''t summon other mephits. It remains for 1 minute, until it or its summoner dies, or until its summoner dismisses it as an action.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (177,'Imp','Tiny','fiend','devil','lawful evil','13','10','3d4','20 ft., fly 40 ft.','6','17','13','11','12','14','','','','','','','','','','4','','3','','','','','','','4','','5','','Infernal, Common','darkvision 120 ft., passive Perception 11','1','','cold; bludgeoning, piercing, and slashing from nonmagical/nonsilver weapons','fire, poison','poisoned','<html><br><li><br><name><text><b>Shapechanger</text></name><br><desc><text><i>The imp can use its action to polymorph into a beast form that resembles a rat (speed 20 ft.), a raven (20 ft., fly 60 ft.), or a spider (20 ft., climb 20 ft.), or back into its true form. Its statistics are the same in each form, except for the speed changes noted. Any equipment it is wearing or carrying isn''t transformed. It reverts to its true form if it dies.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Devil''s Sight</text></name><br><desc><text><i>Magical darkness doesn''t impede the imp''s darkvision.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Magic Resistance</text></name><br><desc><text><i>The imp has advantage on saving throws against spells and other magical effects.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Variant: Familiar</text></name><br><desc><text><i>The imp can serve another creature as a familiar, forming a telepathic bond with its willing master. While the two are bonded, the master can sense what the quasit senses as long as they are within 1 mile of each other. While the imp is within 10 feet of its master, the master shares the quasit''s Magic Resistance trait. At any time and for any reason, the imp can end its service as a familiar, ending the telepathic bond.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Sting (Bite in Beast Form)</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft ., one target. Hit: 5 (1d4 + 3) piercing damage, and the target must make on a DC 11 Constitution saving throw, taking 10 (3d6) poison damage on a failed save, or half as much damage on a successful one.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br><li><br><name><text><b>Invisibility</text></name><br><desc><text><i>The imp magically turns invisible until it attacks, or until its concentration ends (as if concentrating on a spell). Any equipment the imp wears or carries is invisible with it.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (178,'Invisible Stalker','Medium','elemental','','neutral','14','104','16d8','50 ft., fly 50 ft. (hover)','16','19','14','10','15','11','','','','','','','','','','','','','','','','','','8','','','10','','Auran, understands Common but doesn''t speak it','darkvision 60 ft., passive Perception 18','6','','bludgeoning, piercing, and slashing from nonmagical weapons','poison','exhaustion, grappled, paralyzed, petrified, poisoned, prone, restrained, unconscious','<html><br><li><br><name><text><b>Invisibility</text></name><br><desc><text><i>The stalker is invisible.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Faultless Tracker</text></name><br><desc><text><i>The stalker is given a quarry by its summoner. The stalker knows the direction and distance to its quarry as long as the two of them are on the same plane of existence. The stalker also knows the location of its summoner.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The stalker makes two slam attacks.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Slam</text></name><br><desc><text><i>Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 10 (2d6 + 3) bludgeoning damage.</text></desc><br><attack_bonus>6</attack_bonus><br><damage_dice><text>2d6</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (179,'Iron Golem','Large','construct','','unaligned','20','210','20d10','30 ft.','24','9','20','3','11','1','','','','','','','','','','','','','','','','','','','','','','','understands the languages of its creator but can''t speak','darkvision 120 ft., passive Perception 10','16','','','fire, poison, psychic; bludgeoning, piercing, and slashing from nonmagical weapons that aren''t adamantine','charmed, exhaustion, frightened, paralyzed, petrified, poisoned','<html><br><li><br><name><text><b>Fire Absorption</text></name><br><desc><text><i>Whenever the golem is subjected to fire damage, it takes no damage and instead regains a number of hit points equal to the fire damage dealt.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Immutable Form</text></name><br><desc><text><i>The golem is immune to any spell or effect that would alter its form.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Magic Resistance</text></name><br><desc><text><i>The golem has advantage on saving throws against spells and other magical effects.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Magic Weapons</text></name><br><desc><text><i>The golem''s weapon attacks are magical.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The golem makes two melee attacks.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Slam</text></name><br><desc><text><i>Melee Weapon Attack: +13 to hit, reach 5 ft., one target. Hit: 20 (3d8 + 7) bludgeoning damage.</text></desc><br><attack_bonus>13</attack_bonus><br><damage_dice><text>3d8</text></damage_dice><br><damage_bonus>7</damage_bonus><br></li><br><li><br><name><text><b>Sword</text></name><br><desc><text><i>Melee Weapon Attack: +13 to hit, reach 10 ft., one target. Hit: 23 (3d10 + 7) slashing damage.</text></desc><br><attack_bonus>13</attack_bonus><br><damage_dice><text>3d10</text></damage_dice><br><damage_bonus>7</damage_bonus><br></li><br><li><br><name><text><b>Poison Breath (Recharge 5-6)</text></name><br><desc><text><i>The golem exhales poisonous gas in a 15-foot cone. Each creature in that area must make a DC 19 Constitution saving throw, taking 45 (l0d8) poison damage on a failed save, or half as much damage on a successful one.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>10d8</text></damage_dice><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (180,'Jackal','Small','beast','','unaligned','12','3','1d6','40 ft.','8','15','11','3','12','6','','','','','','','','','','','','','','','','','','3','','','','','','passive Perception 13','0','','','','','<html><br><li><br><name><text><b>Keen Hearing and Smell</text></name><br><desc><text><i>The jackal has advantage on Wisdom (Perception) checks that rely on hearing or smell.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Pack Tactics</text></name><br><desc><text><i>The jackal has advantage on an attack roll against a creature if at least one of the jackal''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +1 to hit, reach 5 ft., one target. Hit: 1 (1d4 — 1) piercing damage.</text></desc><br><attack_bonus>1</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br><damage_bonus>-1</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (181,'Killer Whale','Huge','beast','','unaligned','12','90','12d12','swim 60 ft.','19','10','13','3','12','7','','','','','','','','','','','','','','','','','','3','','','','','','blindsight 120 ft., passive Perception 13','3','','','','','<html><br><li><br><name><text><b>Echolocation</text></name><br><desc><text><i>The whale can''t use its blindsight while deafened.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Hold Breath</text></name><br><desc><text><i>The whale can hold its breath for 30 minutes</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Keen Hearing</text></name><br><desc><text><i>The whale has advantage on Wisdom (Perception) checks that rely on hearing.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 21 (5d6 + 4) piercing damage.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (182,'Knight','Medium','humanoid','any race','any alignment','18','52','8d8','30 ft.','16','11','14','11','11','15','','','4','','2','','','','','','','','','','','','','','','','','','any one language (usually Common)','passive Perception 10','3','','','','','<html><br><li><br><name><text><b>Brave</text></name><br><desc><text><i>The knight has advantage on saving throws against being frightened.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','<html><br><li><br><name><text><b>Parry</text></name><br><desc><text><i>The knight adds 2 to its AC against one melee attack that would hit it. To do so, the knight must see the attacker and be wielding a melee weapon.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The knight makes two melee attacks.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Greatsword</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 10 (2d6 + 3) slashing damage.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>2d6</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br><li><br><name><text><b>Heavy Crossbow</text></name><br><desc><text><i>Ranged Weapon Attack: +2 to hit, range 100/400 ft., one target. Hit: 5 (1d10) piercing damage.</text></desc><br><attack_bonus>2</attack_bonus><br><damage_dice><text>1d10</text></damage_dice><br></li><br><li><br><name><text><b>Leadership (Recharges after a Short or Long Rest)</text></name><br><desc><text><i>For 1 minute, the knight can utter a special command or warning whenever a nonhostile creature that it can see within 30 ft. of it makes an attack roll or a saving throw. The creature can add a d4 to its roll provided it can hear and understand the knight. A creature can benefit from only one Leadership die at a time. This effect ends if the knight is incapacitated.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (183,'Kobold','Small','humanoid','kobold','lawful evil','12','5','2d6','30 ft.','7','15','9','8','7','8','','','','','','','','','','','','','','','','','','','','','','','Common, Draconic','darkvision 60 ft., passive Perception 8','1/8','','','','','<html><br><li><br><name><text><b>Sunlight Sensitivity</text></name><br><desc><text><i>While in sunlight, the kobold has disadvantage on attack rolls, as well as on Wisdom (Perception) checks that rely on sight.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Pack Tactics</text></name><br><desc><text><i>The kobold has advantage on an attack roll against a creature if at least one of the kobold''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Dagger</text></name><br><desc><text><i>Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 4 (1d4 + 2) piercing damage.</text></desc><br><attack_bonus>4</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br><damage_bonus>2</damage_bonus><br></li><br><li><br><name><text><b>Sling</text></name><br><desc><text><i>Ranged Weapon Attack: +4 to hit, range 30/120 ft., one target. Hit: 4 (1d4 + 2) bludgeoning damage.</text></desc><br><attack_bonus>4</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br><damage_bonus>2</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (184,'Kraken','Gargantuan','monstrosity','titan','chaotic evil','18','472','27d20','20 ft., swim 60 ft.','30','11','25','22','18','20','17','7','14','13','11','','','','','','','','','','','','','','','','','','understands Abyssal, Celestial, Infernal, and Primordial but can''t speak, telepathy 120 ft.','truesight 120 ft., passive Perception 14','23','','','lightning; bludgeoning, piercing, and slashing from nonmagical weapons','frightened, paralyzed','<html><br><li><br><name><text><b>Amphibious</text></name><br><desc><text><i>The kraken can breathe air and water.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Freedom of Movement</text></name><br><desc><text><i>The kraken ignores difficult terrain, and magical effects can''t reduce its speed or cause it to be restrained. It can spend 5 feet of movement to escape from nonmagical restraints or being grappled.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Siege Monster</text></name><br><desc><text><i>The kraken deals double damage to objects and structures.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The kraken makes three tentacle attacks, each of which it can replace with one use of Fling.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 23 (3d8 + 10) piercing damage. If the target is a Large or smaller creature grappled by the kraken, that creature is swallowed, and the grapple ends. While swallowed, the creature is blinded and restrained, it has total cover against attacks and other effects outside the kraken, and it takes 42 (12d6) acid damage at the start of each of the kraken''s turns. If the kraken takes 50 damage or more on a single turn from a creature inside it, the kraken must succeed on a DC 25 Constitution saving throw at the end of that turn or regurgitate all swallowed creatures, which fall prone in a space within 10 feet of the kraken. If the kraken dies, a swallowed creature is no longer restrained by it and can escape from the corpse using 15 feet of movement, exiting prone.</text></desc><br><attack_bonus>7</attack_bonus><br><damage_dice><text>3d8</text></damage_dice><br><damage_bonus>10</damage_bonus><br></li><br><li><br><name><text><b>Tentacle</text></name><br><desc><text><i>Melee Weapon Attack: +7 to hit, reach 30 ft., one target. Hit: 20 (3d6 + 10) bludgeoning damage, and the target is grappled (escape DC 18). Until this grapple ends, the target is restrained. The kraken has ten tentacles, each of which can grapple one target.</text></desc><br><attack_bonus>7</attack_bonus><br><damage_dice><text>3d6</text></damage_dice><br><damage_bonus>10</damage_bonus><br></li><br><li><br><name><text><b>Fling</text></name><br><desc><text><i>One Large or smaller object held or creature grappled by the kraken is thrown up to 60 feet in a random direction and knocked prone. If a thrown target strikes a solid surface, the target takes 3 (1d6) bludgeoning damage for every 10 feet it was thrown. If the target is thrown at another creature, that creature must succeed on a DC 18 Dexterity saving throw or take the same damage and be knocked prone.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Lightning Storm</text></name><br><desc><text><i>The kraken magically creates three bolts of lightning, each of which can strike a target the kraken can see within 120 feet of it. A target must make a DC 23 Dexterity saving throw, taking 22 (4d10) lightning damage on a failed save, or half as much damage on a successful one.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>4d10</text></damage_dice><br></li><br></html>','<html><br><li><br><name><text><b>Tentacle Attack or Fling</text></name><br><desc><text><i>The kraken makes one tentacle attack or uses its Fling.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Lightning Storm (Costs 2 Actions)</text></name><br><desc><text><i>The kraken uses Lightning Storm.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Ink Cloud (Costs 3 Actions)</text></name><br><desc><text><i>While underwater, the kraken expels an ink cloud in a 60-foot radius. The cloud spreads around corners, and that area is heavily obscured to creatures other than the kraken. Each creature other than the kraken that ends its turn there must succeed on a DC 23 Constitution saving throw, taking 16 (3d10) poison damage on a failed save, or half as much damage on a successful one. A strong current disperses the cloud, which otherwise disappears at the end of the kraken''s next turn.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (185,'Lamia','Large','monstrosity','','chaotic evil','13','97','13d10','30 ft.','16','13','15','14','15','16','','','','','','','','','','7','','4','','','','','','','','','3','','Abyssal, Common','darkvision 60 ft., passive Perception 12','4','','','','','<html><br><li><br><name><text><b>Innate Spellcasting</text></name><br><desc><text><i>The lamia''s innate spellcasting ability is Charisma (spell save DC 13). It can innately cast the following spells, requiring no material components. At will: disguise self (any humanoid form), major image 3/day each: charm person, mirror image, scrying, suggestion 1/day: geas</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The lamia makes two attacks: one with its claws and one with its dagger or Intoxicating Touch.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Claws</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 14 (2d10 + 3) slashing damage.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>2d10</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br><li><br><name><text><b>Dagger</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 5 (1d4 + 3) piercing damage.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br><li><br><name><text><b>Intoxicating Touch</text></name><br><desc><text><i>Melee Spell Attack: +5 to hit, reach 5 ft., one creature. Hit: The target is magically cursed for 1 hour. Until the curse ends, the target has disadvantage on Wisdom saving throws and all ability checks.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (186,'Lemure','Medium','fiend','devil','lawful evil','7','13','3d8','15 ft.','10','5','11','1','11','3','','','','','','','','','','','','','','','','','','','','','','','understands infernal but can''t speak','darkvision 120 ft., passive Perception 10','0','','cold','fire, poison','charmed, frightened, poisoned','<html><br><li><br><name><text><b>Devil''s Sight</text></name><br><desc><text><i>Magical darkness doesn''t impede the lemure''s darkvision.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Hellish Rejuvenation</text></name><br><desc><text><i>A lemure that dies in the Nine Hells comes back to life with all its hit points in 1d10 days unless it is killed by a good-aligned creature with a bless spell cast on that creature or its remains are sprinkled with holy water.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Fist</text></name><br><desc><text><i>Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 2 (1d4) bludgeoning damage</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (187,'Lich','Medium','undead','','any evil alignment','17','135','18d8','30 ft.','11','16','16','20','14','16','','','10','12','9','','','18','','','12','9','','','','','','9','','','','','Common plus up to five other languages','truesight 120 ft., passive Perception 19','21','','cold, lightning, necrotic','poison; bludgeoning, piercing, and slashing from nonmagical weapons','charmed, exhaustion, frightened, paralyzed, poisoned','<html><br><li><br><name><text><b>Legendary Resistance (3/Day)</text></name><br><desc><text><i>If the lich fails a saving throw, it can choose to succeed instead.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Rejuvenation</text></name><br><desc><text><i>If it has a phylactery, a destroyed lich gains a new body in 1d10 days, regaining all its hit points and becoming active again. The new body appears within 5 feet of the phylactery.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Spellcasting</text></name><br><desc><text><i>The lich is an 18th-level spellcaster. Its spellcasting ability is Intelligence (spell save DC 20, +12 to hit with spell attacks). The lich has the following wizard spells prepared:

• Cantrips (at will): mage hand, prestidigitation, ray of frost
• 1st level (4 slots): detect magic, magic missile, shield, thunderwave
• 2nd level (3 slots): detect thoughts, invisibility, Melf''s acid arrow, mirror image
• 3rd level (3 slots): animate dead, counterspell, dispel magic, fireball
• 4th level (3 slots): blight, dimension door
• 5th level (3 slots): cloudkill, scrying
• 6th level (1 slot): disintegrate, globe of invulnerability
• 7th level (1 slot): finger of death, plane shift
• 8th level (1 slot): dominate monster, power word stun
• 9th level (1 slot): power word kill</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Turn Resistance</text></name><br><desc><text><i>The lich has advantage on saving throws against any effect that turns undead.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Paralyzing Touch</text></name><br><desc><text><i>Melee Spell Attack: +12 to hit, reach 5 ft., one creature. Hit: 10 (3d6) cold damage. The target must succeed on a DC 18 Constitution saving throw or be paralyzed for 1 minute. The target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success.</text></desc><br><attack_bonus>12</attack_bonus><br><damage_dice><text>3d6</text></damage_dice><br></li><br></html>','<html><br><li><br><name><text><b>Cantrip</text></name><br><desc><text><i>The lich casts a cantrip.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Paralyzing Touch (Costs 2 Actions)</text></name><br><desc><text><i>The lich uses its Paralyzing Touch.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Frightening Gaze (Costs 2 Actions)</text></name><br><desc><text><i>The lich fixes its gaze on one creature it can see within 10 feet of it. The target must succeed on a DC 18 Wisdom saving throw against this magic or become frightened for 1 minute. The frightened target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success. If a target''s saving throw is successful or the effect ends for it, the target is immune to the lich''s gaze for the next 24 hours.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Disrupt Life (Costs 3 Actions)</text></name><br><desc><text><i>Each living creature within 20 feet of the lich must make a DC 18 Constitution saving throw against this magic, taking 21 (6d6) necrotic damage on a failed save, or half as much damage on a successful one.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>6d6</text></damage_dice><br></li><br></html>',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (188,'Lion','Large','beast','','unaligned','12','26','4d10','50 ft.','17','15','13','3','12','8','','','','','','','','','','','','','','','','','','3','','','6','','','passive Perception 13','1','','','','','<html><br><li><br><name><text><b>Keen Smell</text></name><br><desc><text><i>The lion has advantage on Wisdom (Perception) checks that rely on smell.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Pack Tactics</text></name><br><desc><text><i>The lion has advantage on an attack roll against a creature if at least one of the lion''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Pounce</text></name><br><desc><text><i>If the lion moves at least 20 ft. straight toward a creature and then hits it with a claw attack on the same turn, that target must succeed on a DC 13 Strength saving throw or be knocked prone. If the target is prone, the lion can make one bite attack against it as a bonus action.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Running Leap</text></name><br><desc><text><i>With a 10-foot running start, the lion can long jump up to 25 ft..</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 7 (1d8 + 3) piercing damage.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>1d8</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br><li><br><name><text><b>Claw</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 6 (1d6 + 3) slashing damage.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br></html>','',NULL);
