-- REPLACE INTO `STANDARD_MONSTERS` VALUES (190,'Lizardfolk','Medium','humanoid','lizardfolk','neutral','15','22','4d8','30 ft., swim 30 ft.','15','10','13','7','12','7','','','','','','','','','','','','','','','','','','3','','','4','5','Draconic','passive Perception 13','1/2','','','','','<html><br><li><br><name><text><b>Hold Breath</text></name><br><desc><text><i>The lizardfolk can hold its breath for 15 minutes.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The lizardfolk makes two melee attacks, each one with a different weapon.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 5 (1d6 + 2) piercing damage.</text></desc><br><attack_bonus>4</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>2</damage_bonus><br></li><br><li><br><name><text><b>Heavy Club</text></name><br><desc><text><i>Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 5 (1d6 + 2) bludgeoning damage.</text></desc><br><attack_bonus>4</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>2</damage_bonus><br></li><br><li><br><name><text><b>Javelin</text></name><br><desc><text><i>Melee or Ranged Weapon Attack: +4 to hit, reach 5 ft. or range 30/120 ft., one target. Hit: 5 (1d6 + 2) piercing damage.</text></desc><br><attack_bonus>4</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>2</damage_bonus><br></li><br><li><br><name><text><b>Spiked Shield</text></name><br><desc><text><i>Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 5 (1d6 + 2) piercing damage.</text></desc><br><attack_bonus>4</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>2</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (191,'Mage','Medium','humanoid','any race','any alignment','12','40','9d8','30 ft.','9','14','11','17','12','11','','','','6','4','','','6','','','6','','','','','','','','','','','','any four languages','passive Perception 11','6','','','','','<html><br><li><br><name><text><b>Spellcasting</text></name><br><desc><text><i>The mage is a 9th-level spellcaster. Its spellcasting ability is Intelligence (spell save DC 14, +6 to hit with spell attacks). The mage has the following wizard spells prepared:

