-- REPLACE INTO `STANDARD_MONSTERS` VALUES (242,'Roc','Gargantuan','monstrosity','','unaligned','15','248','16d20','20 ft., fly 120 ft.','28','10','20','3','10','9','','4','9','','4','3.0','','','','','','','','','','','','4','','','','','','passive Perception 14','11','','','','','<html><br><li><br><name><text><b>Keen Sight</text></name><br><desc><text><i>The roc has advantage on Wisdom (Perception) checks that rely on sight.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The roc makes two attacks: one with its beak and one with its talons.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Beak</text></name><br><desc><text><i>Melee Weapon Attack: +13 to hit, reach 10 ft., one target. Hit: 27 (4d8 + 9) piercing damage.</text></desc><br><attack_bonus>13</attack_bonus><br><damage_dice><text>4d8</text></damage_dice><br><damage_bonus>9</damage_bonus><br></li><br><li><br><name><text><b>Talons</text></name><br><desc><text><i>Melee Weapon Attack: +13 to hit, reach 5 ft., one target. Hit: 23 (4d6 + 9) slashing damage, and the target is grappled (escape DC 19). Until this grapple ends, the target is restrained, and the roc can''t use its talons on another target.</text></desc><br><attack_bonus>13</attack_bonus><br><damage_dice><text>4d6</text></damage_dice><br><damage_bonus>9</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (243,'Roper','Large','monstrosity','','neutral evil','20','93','11d10','10 ft., climb 10 ft.','18','8','17','7','16','6','','','','','','','','','','','','','','','','','','6','','','5','','','darkvision 60 ft., passive Perception 16','5','','','','','<html><br><li><br><name><text><b>False Appearance</text></name><br><desc><text><i>While the roper remains motionless, it is indistinguishable from a normal cave formation, such as a stalagmite.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Grasping Tendrils</text></name><br><desc><text><i>The roper can have up to six tendrils at a time. Each tendril can be attacked (AC 20; 10 hit points; immunity to poison and psychic damage). Destroying a tendril deals no damage to the roper, which can extrude a replacement tendril on its next turn. A tendril can also be broken if a creature takes an action and succeeds on a DC 15 Strength check against it.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Spider Climb</text></name><br><desc><text><i>The roper can climb difficult surfaces, including upside down on ceilings, without needing to make an ability check.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The roper makes four attacks with its tendrils, uses Reel, and makes one attack with its bite.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 22 (4d8 + 4) piercing damage.</text></desc><br><attack_bonus>7</attack_bonus><br><damage_dice><text>4d8</text></damage_dice><br><damage_bonus>4</damage_bonus><br></li><br><li><br><name><text><b>Tendril</text></name><br><desc><text><i>Melee Weapon Attack: +7 to hit, reach 50 ft., one creature. Hit: The target is grappled (escape DC 15). Until the grapple ends, the target is restrained and has disadvantage on Strength checks and Strength saving throws, and the roper can''t use the same tendril on another target.</text></desc><br><attack_bonus>7</attack_bonus><br></li><br><li><br><name><text><b>Reel</text></name><br><desc><text><i>The roper pulls each creature grappled by it up to 25 ft. straight toward it.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (244,'Rug of Smothering','Large','construct','','unaligned','12','33','6d10','10 ft.','17','14','10','1','3','1','','','','','','','','','','','','','','','','','','','','','','','','blindsight 60 ft. (blind beyond this radius), passive Perception 6','2','','','poison, psychic','blinded, charmed, deafened, frightened, paralyzed, petrified, poisoned','<html><br><li><br><name><text><b>Antimagic Susceptibility</text></name><br><desc><text><i>The rug is incapacitated while in the area of an antimagic field. If targeted by dispel magic, the rug must succeed on a Constitution saving throw against the caster''s spell save DC or fall unconscious for 1 minute.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Damage Transfer</text></name><br><desc><text><i>While it is grappling a creature, the rug takes only half the damage dealt to it, and the creature grappled by the rug takes the other half.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>False Appearance</text></name><br><desc><text><i>While the rug remains motionless, it is indistinguishable from a normal rug.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Smother</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft., one Medium or smaller creature. Hit: The creature is grappled (escape DC 13). Until this grapple ends, the target is restrained, blinded, and at risk of suffocating, and the rug can''t smother another target. In addition, at the start of each of the target''s turns, the target takes 10 (2d6 + 3) bludgeoning damage.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>2d6</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (245,'Rust Monster','Medium','monstrosity','','unaligned','14','27','5d8','40 ft.','13','12','13','2','13','6','','','','','','','','','','','','','','','','','','','','','','','','darkvision 60 ft., passive Perception 11','1/2','','','','','<html><br><li><br><name><text><b>Iron Scent</text></name><br><desc><text><i>The rust monster can pinpoint, by scent, the location of ferrous metal within 30 feet of it.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Rust Metal</text></name><br><desc><text><i>Any nonmagical weapon made of metal that hits the rust monster corrodes. After dealing damage, the weapon takes a permanent and cumulative -1 penalty to damage rolls. If its penalty drops to -5, the weapon is destroyed. Non magical ammunition made of metal that hits the rust monster is destroyed after dealing damage.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 5 (1d8 + 1) piercing damage.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d8</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br><li><br><name><text><b>Antennae</text></name><br><desc><text><i>The rust monster corrodes a nonmagical ferrous metal object it can see within 5 feet of it. If the object isn''t being worn or carried, the touch destroys a 1-foot cube of it. If the object is being worn or carried by a creature, the creature can make a DC 11 Dexterity saving throw to avoid the rust monster''s touch.
If the object touched is either metal armor or a metal shield being worn or carried, its takes a permanent and cumulative -1 penalty to the AC it offers. Armor reduced to an AC of 10 or a shield that drops to a +0 bonus is destroyed. If the object touched is a held metal weapon, it rusts as described in the Rust Metal trait.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (246,'Saber-Toothed Tiger','Large','beast','','unaligned','12','52','7d10','40 ft.','18','14','15','3','12','8','','','','','','','','','','','','','','','','','','3','','','6','','','passive Perception 13','2','','','','','<html><br><li><br><name><text><b>Keen Smell</text></name><br><desc><text><i>The tiger has advantage on Wisdom (Perception) checks that rely on smell.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Pounce</text></name><br><desc><text><i>If the tiger moves at least 20 ft. straight toward a creature and then hits it with a claw attack on the same turn, that target must succeed on a DC 14 Strength saving throw or be knocked prone. If the target is prone, the tiger can make one bite attack against it as a bonus action.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 10 (1d10 + 5) piercing damage.</text></desc><br><attack_bonus>6</attack_bonus><br><damage_dice><text>1d10</text></damage_dice><br><damage_bonus>5</damage_bonus><br></li><br><li><br><name><text><b>Claw</text></name><br><desc><text><i>Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 12 (2d6 + 5) slashing damage.</text></desc><br><attack_bonus>6</attack_bonus><br><damage_dice><text>2d6</text></damage_dice><br><damage_bonus>5</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (247,'Sahuagin','Medium','humanoid','sahuagin','lawful evil','12','22','4d8','30 ft., swim 40 ft.','13','11','12','12','13','9','','','','','','','','','','','','','','','','','','5','','','','','Sahuagin','darkvision 120 ft., passive Perception 15','1/2','','','','','<html><br><li><br><name><text><b>Blood Frenzy</text></name><br><desc><text><i>The sahuagin has advantage on melee attack rolls against any creature that doesn''t have all its hit points.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Limited Amphibiousness</text></name><br><desc><text><i>The sahuagin can breathe air and water, but it needs to be submerged at least once every 4 hours to avoid suffocating.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Shark Telepathy</text></name><br><desc><text><i>The sahuagin can magically command any shark within 120 feet of it, using a limited telepathy.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The sahuagin makes two melee attacks: one with its bite and one with its claws or spear.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Bite</text></name><br><desc><text><i>Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 3 (1d4 + 1) piercing damage.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br><li><br><name><text><b>Claws</text></name><br><desc><text><i>Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 3 (1d4 + 1) slashing damage.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d4</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br><li><br><name><text><b>Spear</text></name><br><desc><text><i>Melee or Ranged Weapon Attack: +3 to hit, reach 5 ft. or range 20/60 ft., one target. Hit: 4 (1d6 + 1) piercing damage, or 5 (1d8 + 1) piercing damage if used with two hands to make a melee attack.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (248,'Salamander','Large','elemental','','neutral evil','15','90','12d10','30 ft.','18','14','15','11','10','12','','','','','','','','','','','','','','','','','','','','','','','Ignan','darkvision 60 ft., passive Perception 10','5','cold','bludgeoning, piercing, and slashing from nonmagical weapons','fire','','<html><br><li><br><name><text><b>Heated Body</text></name><br><desc><text><i>A creature that touches the salamander or hits it with a melee attack while within 5 ft. of it takes 7 (2d6) fire damage.</text></desc><br><attack_bonus>0</attack_bonus><br><damage_dice><text>2d6</text></damage_dice><br></li><br><li><br><name><text><b>Heated Weapons</text></name><br><desc><text><i>Any metal melee weapon the salamander wields deals an extra 3 (1d6) fire damage on a hit (included in the attack).</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The salamander makes two attacks: one with its spear and one with its tail.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Spear</text></name><br><desc><text><i>Melee or Ranged Weapon Attack: +7 to hit, reach 5 ft. or range 20 ft./60 ft., one target. Hit: 11 (2d6 + 4) piercing damage, or 13 (2d8 + 4) piercing damage if used with two hands to make a melee attack, plus 3 (1d6) fire damage.</text></desc><br><attack_bonus>7</attack_bonus><br><damage_dice><text>2d6</text></damage_dice><br><damage_bonus>4</damage_bonus><br></li><br><li><br><name><text><b>Tail</text></name><br><desc><text><i>Melee Weapon Attack: +7 to hit, reach 10 ft., one target. Hit: 11 (2d6 + 4) bludgeoning damage plus 7 (2d6) fire damage, and the target is grappled (escape DC 14). Until this grapple ends, the target is restrained, the salamander can automatically hit the target with its tail, and the salamander can''t make tail attacks against other targets.</text></desc><br><attack_bonus>7</attack_bonus><br><damage_dice><text>2d6 + 2d6</text></damage_dice><br><damage_bonus>4</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (249,'Satyr','Medium','fey','','chaotic neutral','14','31','7d8','40 ft.','12','16','11','12','10','14','','','','','','','','','','','','','','','','','6','2','','','5','','Common, Elvish, Sylvan','passive Perception 12','1/2','','','','','<html><br><li><br><name><text><b>Magic Resistance</text></name><br><desc><text><i>The satyr has advantage on saving throws against spells and other magical effects.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Ram</text></name><br><desc><text><i>Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 6 (2d4 + 1) bludgeoning damage.</text></desc><br><attack_bonus>3</attack_bonus><br><damage_dice><text>2d4</text></damage_dice><br><damage_bonus>1</damage_bonus><br></li><br><li><br><name><text><b>Shortsword</text></name><br><desc><text><i>Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 6 (1 d6 + 3) piercing damage.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br><li><br><name><text><b>Shortbow</text></name><br><desc><text><i>Ranged Weapon Attack: +5 to hit, range 80/320 ft., one target. Hit: 6 (1d6 + 3) piercing damage.</text></desc><br><attack_bonus>5</attack_bonus><br><damage_dice><text>1d6</text></damage_dice><br><damage_bonus>3</damage_bonus><br></li><br><li><br><name><text><b>Variant: Panpipes</text></name><br><desc><text><i>Gentle Lullaby. The creature falls asleep and is unconscious for 1 minute. The effect ends if the creature takes damage or if someone takes an action to shake the creature awake.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','',NULL);
