-- REPLACE INTO `STANDARD_MONSTERS` VALUES (268,'Stone Giant','Huge','giant','','neutral','17','126','11d12','40 ft.','23','15','20','10','12','9','','5','8','','4','','','','12','','','','','','','','','4','','','','','Giant','darkvision 60 ft., passive Perception 14','7','','','','','<html><br><li><br><name><text><b>Stone Camouflage</text></name><br><desc><text><i>The giant has advantage on Dexterity (Stealth) checks made to hide in rocky terrain.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','<html><br><li><br><name><text><b>Rock Catching</text></name><br><desc><text><i>If a rock or similar object is hurled at the giant, the giant can, with a successful DC 10 Dexterity saving throw, catch the missile and take no bludgeoning damage from it.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The giant makes two greatclub attacks.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Greatclub</text></name><br><desc><text><i>Melee Weapon Attack: +9 to hit, reach 15 ft., one target. Hit: 19 (3d8 + 6) bludgeoning damage.</text></desc><br><attack_bonus>9</attack_bonus><br><damage_dice><text>3d8</text></damage_dice><br><damage_bonus>6</damage_bonus><br></li><br><li><br><name><text><b>Rock</text></name><br><desc><text><i>Ranged Weapon Attack: +9 to hit, range 60/240 ft., one target. Hit: 28 (4d10 + 6) bludgeoning damage. If the target is a creature, it must succeed on a DC 17 Strength saving throw or be knocked prone.</text></desc><br><attack_bonus>9</attack_bonus><br><damage_dice><text>4d10</text></damage_dice><br><damage_bonus>6</damage_bonus><br></li><br></html>','',NULL);
-- REPLACE INTO `STANDARD_MONSTERS` VALUES (269,'Stone Golem','Large','construct','','unaligned','17','178','17d10','30 ft.','22','9','20','3','11','1','','','','','','','','','','','','','','','','','','','','','','','understands the languages of its creator but can''t speak','darkvision 120 ft., passive Perception 10','10','','','poison, psychic; bludgeoning, piercing, and slashing from nonmagical weapons that aren''t adamantine','charmed, exhaustion, frightened, paralyzed, petrified, poisoned','<html><br><li><br><name><text><b>Immutable Form</text></name><br><desc><text><i>The golem is immune to any spell or effect that would alter its form.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Magic Resistance</text></name><br><desc><text><i>The golem has advantage on saving throws against spells and other magical effects.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Magic Weapons</text></name><br><desc><text><i>The golem''s weapon attacks are magical.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br></html>','','<html><br><li><br><name><text><b>Multiattack</text></name><br><desc><text><i>The golem makes two slam attacks.</text></desc><br><attack_bonus>0</attack_bonus><br></li><br><li><br><name><text><b>Slam</text></name><br><desc><text><i>Melee Weapon Attack: +10 to hit, reach 5 ft., one target. Hit: 19 (3d8 + 6) bludgeoning damage.</text></desc><br><attack_bonus>10</attack_bonus><br><damage_dice><text>3d8</text></damage_dice><br><damage_bonus>6</damage_bonus><br></li><br><li><br><name><text><b>Slow (Recharge 5-6)</text></name><br><desc><text><i>The golem targets one or more creatures it can see within 10 ft. of it. Each target must make a DC 17 Wisdom saving throw against this magic. On a failed save, a target can''t use reactions, its speed is halved, and it can''t make more than one attack on its turn. In addition, the target can take either an action or a bonus action on its turn,